import React, { useContext, useState } from 'react';
import AuthContext from '../context/AuthContext';
import { Button, TextField, Grid, Paper, Typography, Link } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { useNavigate } from 'react-router-dom';

const useStyles = makeStyles({
  paper: {
    padding: 20,
    height: '50vh',
    width: 280,
    margin: '20px auto',
  },
  btn: {
    marginTop: 20,
  },
});

const Loginpage = () => {
    const navigate=useNavigate();
    const { loginUser } = useContext(AuthContext);
    const classes = useStyles();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

  


  const handleSubmit = (e) => {
    e.preventDefault();
    loginUser(username, password);
  };


  return (
    <Grid>
      <Paper elevation={10} className={classes.paper}>
        <Grid align='center'>
          <Typography variant='h4' color='primary'>
            Login
          </Typography>
        </Grid>
        <form onSubmit={handleSubmit}>
          <TextField label='Username' placeholder='Enter username' fullWidth required onChange={(e) => setUsername(e.target.value)} />
          <TextField label='Password' placeholder='Enter password' type='password' fullWidth required onChange={(e) => setPassword(e.target.value)} />
          <Button type='submit' color='primary' variant='contained' className={classes.btn} fullWidth>
            Submit
          </Button>
        </form>
        <Typography>
            <br />
          <Link href="/register">
            Don't have an account? Register
          </Link>
        </Typography>
      </Paper>
    </Grid>
  );
};


export default Loginpage;
